import React, {Component} from 'react';

import { withStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import ListItemText from '@material-ui/core/ListItemText';
import IconButton from '@material-ui/core/IconButton';
import CommentIcon from '@material-ui/icons/Comment';
import EditIcon from '@material-ui/icons/Edit';
import DeleteIcon from '@material-ui/icons/Delete';
import Loading from './LoadingComponent';

class ListaUsuarios extends Component{
	toggleModal(user){
		alert(user.name);
		console.log(user);
		alert("user");
	}
	
	render(){
		
		if(this.props.usersLoading){
			return (
				<div style = {{
					
					margin: '0 auto'
				}}>
					<Loading />
				</div>
			);
		}else if(this.props.usersFailed){
			return(<p>Error</p>);
		}else if(this.props.users != null){
			return (
			  <div className="listaUsuarios">
				<List>
				  {this.props.users.map(user => (
					<ListItem key={user.id} role={undefined} dense button>
					  
					  <ListItemText primary={`${user.name}  ${user.lastName}
					  `} />
					  <ListItemSecondaryAction>						
						<IconButton aria-label="Comments" onClick= {(user) => this.toggleModal(user)}>
						  <EditIcon/>
						</IconButton>
						<IconButton aria-label="Comments">
						  <DeleteIcon />
						</IconButton>
					  </ListItemSecondaryAction>
					</ListItem>
				  ))}
				</List>
			  </div>
			);
		}
		
	}
}

export default ListaUsuarios;
