import React, { Component } from 'react';
import './App.css';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Paper from '@material-ui/core/Paper';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';
import Modal from '@material-ui/core/Modal';
import ListaUsuarios from './Usuarios.js';
import MaterialUIForm from './MaterialUIForm'
import {actions} from 'react-redux-form';
import {connect} from 'react-redux';
import {postUser,fetchUsers} from './redux/ActionCreators';
import { Values } from 'redux-form-website-template';
import axios from 'axios';


  const mapStateToProps = state =>{
		return {
			users: state.users
		}
	}
  
const mapDispatchToProps = (dispatch) => ({
	postUser:(name, lastName, age, email) => dispatch(postUser(name, lastName, age, email)),
	resetFeedbackForm: () => {dispatch(actions.reset('feedback'))},
	fetchUsers: () => {dispatch(fetchUsers())}
});



class Main extends Component {
	
	
	componentDidMount(){
		this.props.fetchUsers();
	}
	
	
	
	
	constructor(props){
		super(props);
		this.state = {modalOpen: false};
	}

  

  	handleSubmit = (values) => {
		alert(values.firstName + " " + values.lastName + " " + values.age + " " + values.email);
		this.props.postUser(values.name, values.lastName, values.age, values.email);
		//this.setState({modalOpen: !this.state.modalOpen});
	}
  
  

  
  render() {
    return (
      <div >
        <AppBar position="static" color="default">
          <Toolbar>
            <Typography variant="h6" color="inherit">
              Test React
            </Typography>
          </Toolbar>
        </AppBar>
        <div style={{
            width: '60%',
            margin: '0 auto',
            marginTop: 50,
          }}>
          <Paper>
            <div style={{
              minHeight: 200,
              padding: 25
            }}>
            <Typography variant="title" color="inherit" style={{display: 'inline'}}>Listado de usuarios</Typography>
            {' '}
			<Button variant="fab" color="primary" aria-label="Add" style={{ width: 35, height: 30}} onClick={this.toggleModal}>
              <AddIcon />
			  
            </Button>
			
				<div >
				
					<ListaUsuarios
						users = {this.props.users.users}
						usersLoading = {this.props.users.usersLoading}
						usersFailed =  {this.props.users.usersFailed}
					/>
					<Modal
					  aria-labelledby="simple-modal-title"
					  aria-describedby="simple-modal-description"
					  open={this.state.modalOpen}
					  onClose={this.toggleModal}>
						
					<Paper className="modal">	
						<div>
							<Typography variant="h6" id="modal-title">
							  Text in a modal
							</Typography>
							<Typography variant="subtitle1" id="simple-modal-description">
							  Duis mollis, est non commodo luctus, nisi erat porttitor ligula.
							</Typography>
							<MaterialUIForm postUser={this.props.postUser} onSubmit={this.handleSubmit}/>
							<Values form="MaterialUIForm" />
						</div>
					</Paper>
					
					
					</Modal>
				</div>
            
			
			
			</div>
          </Paper>
        </div>
      </div>
    );
  }
}


export default connect(mapStateToProps, mapDispatchToProps)(Main);
